function [cdx1,cdx] = center(p,draw)%% CENTER   Determine index of center node%%             cdx = center(p)%             cdx = center(p,'r')     % draw%   if (nargin < 2)      draw = '';   end      [Ix,Iy] = direction(p);      % direction matrices   asumx = abs(sum(Ix));   asumy = abs(sum(Iy'));      for (i=0:length(p))      ix = (asumx <= min(asumx) + i);      iy = (asumy <= min(asumy) + i);      cdx = find(ix & iy);            if (~isempty(cdx))         cdx1 = cdx(1);         if ( ~isempty(draw) )            circle(p,cdx1,'r',0.5,3);         end         return      end   end      error('assert');             % must never come hereend
