function circle(p,idx,col,r,lw)%% CIRCLE    Marked indexed slocs with a circle of given radius, color and line%           width%              circle(p,idx,r,col,lw)%              circle(p,idx,0.5,'r',1)%              circle(p,idx)              % r=0.5, col='r', lw=1 %   if (nargin < 3)      col = 'r';                       % default color   end   if (nargin < 4)      r = 0.5;                         % default radius   end   if (nargin < 5)      lw = 1;                          % default line width   end      hold on;   phi = 0:pi/50:2*pi;   for (k=1:length(idx))      j = idx(k);      h = plot(p(1,j)+r*cos(phi), p(2,j)+r*sin(phi),col);      set(h,'linewidth',lw);   endend
