function db = dbdata(d)%% DBDATA    dB measurement data for various BLE advertising dB measurements%%               db = mdbdata(d)     % d = distance%%            Support fuer d: 0.5, 1.0, 2.0, 3.0, 5.0, 7.0%   switch (d)      case 0.5         db = -[45 48 44 48 43 46 40 50 51 43 45 43 39 46 43 50 43 46 ...                  47 48 43 45 42 44 42 44 42 39 40];         return      case 1.0         db = -[62 65 58 60 60 59 60 59 59 58 ...                64 58 57 59 58 56 60 57 63 58 ...                62 59 61 59 60 56 60 57 56 56 ];         return      case 2.0         db = -[50 54 55 56 54 53 54 53 60 55 54 54 54 55 54 56 54 55 55 54];         return      case 3.0         db = -[66 59 60 57 59 60 58 57 56 59 57 55 57 55 56 54 60 57 ...                60 55 56 57 59 58];              return      case 5.0         db = -[74 82 71 90 72 74 73 76 84 86 78 89 76 80 82 73 75 ...                84 89 73 74 76 80 79 80 87 74];              return      case 7.0         db = -[64 67 73 91 67 90 72 92 72 67 68 70 74 65 73 66 71 69 ...                70 68 73 65 68 65 71 66];              return      otherwise         error('bad distance');   end
end
